mapper.select:
SELECT id, date, weight as bodyWeight FROM training.weight_records
user_id=#{userId}
AND (DATE_FORMAT(weight_records.date, '%Y/%m/%d') LIKE CONCAT('%', #{searchName}, '%') OR weight_records.weight LIKE CONCAT('%', #{searchName}, '%') )
ORDER BY date DESC, id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM training.weight_records
weight_records.user_id = #{userId}
AND (DATE_FORMAT(weight_records.date, '%Y/%m/%d') LIKE CONCAT('%', #{searchName}, '%') OR weight_records.weight LIKE CONCAT('%', #{searchName}, '%') )

mapper.update:
UPDATE
	training.weight_records
SET
	weight_records.date=#{date}
	,weight_records.weight=#{bodyWeight}
WHERE
	id=#{id}
